abstract class SortAlgorithm {
    // Template method to define the sorting algorithm's skeleton
    public abstract void sort(int[] array);

    // Abstract method to compare elements during sorting
    public abstract boolean compare(int i, int j, int array[]);

    // Abstract method to swap elements if necessary
    public abstract void swap(int i, int j, int array[]);
}

// Concrete subclass for Bubble Sort
class BubbleSort extends SortAlgorithm {
    // Implement the sort method according to Bubble Sort algorithm
    @Override
    public void sort(int[] array) {
        int n = array.length;
        for (int i = 0; i < n - 1; i++) {
            for (int j = 0; j < n - i - 1; j++) {
                if (compare(j, j + 1, array)) {
                    swap(j, j + 1, array);
                }
            }
        }
    }

    // Implement the compare method for Bubble Sort
    @Override
    public boolean compare(int i, int j, int array[]) {
        return array[i] > array[j];
    }

    // Implement the swap method for Bubble Sort
    @Override
    public void swap(int i, int j, int array[]) {
        int temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
}

