    // Implement the sort method according to Selection Sort algorithm
    @Override
    public void sort(int[] array) {
        int n = array.length;
        for (int i = 0; i < n - 1; i++) {
            int minIndex = i;
            for (int j = i + 1; j < n; j++) {
                if (compare(j, minIndex, array)) {
                    minIndex = j;
                }
            }
