import abc

# Abstract class for sorting algorithms
class SortAlgorithm(abc.ABC):
    @abc.abstractmethod
    def sort(self, array):
        pass

    @abc.abstractmethod
    def compare(self, i, j, array):
        pass

    @abc.abstractmethod
    def swap(self, i, j, array):
        pass

# Concrete subclass for BubbleSort
class BubbleSort(SortAlgorithm):
    def sort(self, array):
        n = len(array)
        for i in range(n - 1):
            for j in range(n - i - 1):
                if self.compare(j, j + 1, array):
                    self.swap(j, j + 1, array)

    def compare(self, i, j, array):
        return array[i] > array[j]

    def swap(self, i, j, array):
        array[i], array[j] = array[j], array[i]

