        n = len(array)
        for i in range(n - 1):
            min_index = i
            for j in range(i + 1, n):
                if self.compare(j, min_index, array):
                    min_index = j
            self.swap(i, min_index, array)

    def compare(self, i, j, array):
        return array[i] > array[j]

