from sort_algorithm import SortAlgorithm

class SelectionSort(SortAlgorithm):
    def sort(self, array):
        n = len(array)
        for i in range(n):
            min_idx = i
            for j in range(i+1, n):
                if self.compare(array[j], array[min_idx]) < 0:
                    min_idx = j
            self.swap(i, min_idx)

    def compare(self, a, b):
        return a < b

    def swap(self, i, j):
        array[i], array[j] = array[j], array[i]
