// Abstract class for sorting algorithms
abstract class SortAlgorithm {
    // Template method to define the sorting algorithm's skeleton
    public abstract void sort(int[] array);

    // Abstract method to compare elements during sorting
    public abstract boolean compare(int i, int j, int array);

    // Abstract method to swap elements if necessary
    public abstract void swap(int i, int j, int array);
}

// Concrete subclass for BubbleSort
class BubbleSort extends SortAlgorithm {
    // Implement the sort method according to the chosen sorting algorithm
    @Override
    public void sort(int[] array) {
        int n = array.length;
        for (int i = 0; i < n - 1; i++) {
            for (int j = 0; j < n - i - 1; j++) {
                if (compare(j, j + 1, array)) {
                    swap(j, j + 1, array);
                }
            }
        }
    }

    // Implement the compare method for BubbleSort
    @Override
    public boolean compare(int i, int j, int array) {
        return array[i] > array[j];
    }

    // Implement the swap method for BubbleSort
    @Override
    public void swap(int i, int j, int array) {
        int temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
}

// Concrete subclass for SelectionSort
class SelectionSort extends SortAlgorithm {
    // Implement the sort method according to the chosen sorting algorithm
    @Override
    public void sort(int[] array) {
        int n = array.length;
        for (int i = 0; i < n - 1; i++) {
            int minIndex = i;
            for (int j = i + 1; j < n; j++) {
                if (compare(j, minIndex, array)) {
                    minIndex = j;
                }
            }
            swap(i, minIndex, array);
        }
    }

    // Implement the compare method for SelectionSort
    @Override
    public boolean compare(int i, int j, int array) {
        return array[i] > array[j];
    }

    // Implement the swap method for SelectionSort
    @Override
    public void swap(int i, int j, int array) {
        int temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
}
