abstract class SortAlgorithm {
    public abstract void sort(int[] array);

    // Template method: sort()
    public void sort(int[] array) {
        // Call the compare and swap methods
        compare(array, 0, array.length - 1);
    }

    // Abstract method: compare()
    abstract void compare(int[] array, int i, int j);

    // Abstract method: swap()
    abstract void swap(int[] array, int i, int j);
}

