def check_alphabetical(strings):
    results = []
    for s in strings:
        s = s.lower()
        is_ordered = True
        for i in range(len(s)-1):
            if s[i] > s[i+1]:
                is_ordered = False
                break
        results.append("Yes" if is_ordered else "No")
    return results

# Example usage
