from collections import deque

def is_perfect_matching_possible(boys, girls):
    # Create adjacency list for boys
    graph = {}
    for boy in boys:
        graph[boy] = [girl for girl in girls if girl != boy]

    # Hopcroft-Karp algorithm implementation
    def hopcroft_karp():
        pair_u = {u: None for u in graph}
        pair_v = {v: None for v in girls}
        dist = {}

        def bfs():
            queue = deque()
            for u in graph:
                if pair_u[u] is None:
                    dist[u] = 0
                    queue.append(u)
                else:
                    dist[u] = float('inf')
            dist[None] = float('inf')
            while queue:
                u = queue.popleft()
                if u is not None:
                    for v in graph[u]:
                        if dist[pair_v[v]] == float('inf'):
                            dist[pair_v[v]] = dist[u] + 1
                            queue.append(pair_v[v])
            return dist[None] != float('inf')

        def dfs(u):
            if u is not None:
                for v in graph[u]:
                    if dist[pair_v[v]] == dist[u] + 1:
                        if dfs(pair_v[v]):
                            pair_u[u] = v
                            pair_v[v] = u
                            return True
                dist[u] = float('inf')
                return False
            return True

        result = 0
        while bfs():
            for u in graph:
                if pair_u[u] is None:
                    if dfs(u):
                        result += 1
        return result

    max_matching = hopcroft_karp()
    return max_matching == len(boys)

