            if len(parts) != 3:
                raise ValueError("Please enter exactly two positive integers and an operator.")
            a = int(parts[0])
            b = int(parts[1])
            operator = parts[2]

            # Validate input
            if a <= 0 or b <= 0:
                raise ValueError("Both operands must be positive integers.")
            if operator not in ['+', '-', '*', '/']:
                raise ValueError("Invalid operator. Please use one of '+', '-', '*', or '/'.")
            if operator == '/' and b == 0:
                raise ValueError("Cannot divide by zero.")

            # Calculate and print the result
            result = calculate(a, b, operator)
            print(f"The result of {a} {operator} {b} is: {result}")

        except ValueError as e:
            print(f"Error: {e}")

