// Abstract Class: SortAlgorithm
public abstract class SortAlgorithm<T> {
    public final void sort(T[] array) {
        for (int i = 0; i < array.length - 1; i++) {
            for (int j = 0; j < array.length - i - 1; j++) {
                if (compare(array[j], array[j + 1]) > 0) {
                    swap(array, j, j + 1);
                }
            }
        }
    }

    protected abstract int compare(T a, T b);

    protected void swap(T[] array, int i, int j) {
        T temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
}

// Concrete Subclass: BubbleSort
class BubbleSort<T extends Comparable<T>> extends SortAlgorithm<T> {
    @Override
    protected int compare(T a, T b) {
        return a.compareTo(b);
    }
}

// Concrete Subclass: SelectionSort
class SelectionSort<T extends Comparable<T>> extends SortAlgorithm<T> {
    @Override
    protected int compare(T a, T b) {
        return a.compareTo(b);
    }
}

// Example Usage
public class Main {
    public static void main(String[] args) {
        Integer[] integers = {5, 2, 8, 1};
        BubbleSort<Integer> bubble = new BubbleSort<>();
        bubble.sort(integers);
        System.out.println("Sorted Integers: " + java.util.Arrays.toString(integers));

        String[] strings = {"dog", "cat", "apple"};
        SelectionSort<String> selection = new SelectionSort<>();
        selection.sort(strings);
        System.out.println("Sorted Strings: " + java.util.Arrays.toString(strings));
    }
}
