**Implementation of the Template Method Design Pattern for Sorting Algorithms**

```java
// Abstract Class: SortAlgorithm
public abstract class SortAlgorithm<T> {
    public abstract void sort(T[] array);

    protected abstract int compare(T a, T b);
    protected abstract void swap(T[] array, int i, int j);
}

// Concrete Subclass: BubbleSort
class BubbleSort<T extends Comparable<T>> extends SortAlgorithm<T> {
    @Override
    public void sort(T[] array) {
        for (int i = 0; i < array.length - 1; i++) {
            for (int j = 0; j < array.length - i - 1; j++) {
                if (compare(array[j], array[j + 1]) > 0) {
                    swap(array, j, j + 1);
                }
            }
        }
    }

    @Override
    protected int compare(T a, T b) {
        return ((Comparable<T>) a).compareTo(b);
    }

    @Override
    protected void swap(T[] array, int i, int j) {
        T temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
}

// Concrete Subclass: SelectionSort
class SelectionSort<T extends Comparable<T>> extends SortAlgorithm<T> {
    @Override
    public void sort(T[] array) {
        for (int i = 0; i < array.length - 1; i++) {
            int minIndex = i;
            for (int j = i + 1; j < array.length; j++) {
                if (compare(array[j], array[minIndex]) < 0) {
                    minIndex = j;
                }
            }
            swap(array, i, minIndex);
        }
    }

    @Override
    protected int compare(T a, T b) {
        return ((Comparable<T>) a).compareTo(b);
    }

    @Override
    protected void swap(T[] array, int i, int j) {
        T temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
}

// Example Usage
public class Main {
    public static void main(String[] args) {
        Integer[] integers = {5, 2, 8, 1};
        BubbleSort<Integer> bubble = new BubbleSort<>();
        bubble.sort(integers);
        System.out.println(Arrays.toString(integers)); // Output: [1, 2, 5, 8]

        String[] strings = {"dog", "cat", "apple"};
        SelectionSort<String> selection = new SelectionSort<>();
        selection.sort(strings);
        System.out.println(Arrays.toString(strings)); // Output: [apple, cat, dog]
    }
}
