import cmath

def calculate_quadratic_roots(a, b, c):
    """
    Calculates the roots of a quadratic equation ax^2 + bx + c = 0.

    Args:
        a (float): Coefficient of x^2.
        b (float): Coefficient of x.
        c (float): Constant term.

    Returns:
        tuple: A tuple containing the roots.
               - If two distinct real roots: (x1, x2)
               - If one real root: (x,)
               - If complex conjugate roots: (complex_x1, complex_x2)
               - If 'a' is zero (linear equation): (x,) or "No solution"
    """
    if a == 0:
        if b == 0:
            if c == 0:
                return "Infinite solutions (0=0)"
            else:
                return "No solution (c=0, c!=0)"
        else:
            # Linear equation: bx + c = 0  => x = -c/b
            x = -c / b
            return (x,)

    # Calculate the discriminant
    discriminant = (b**2) - 4*(a*c)

    if discriminant > 0:
        # Two distinct real roots
        x1 = (-b - discriminant**0.5) / (2*a)
        x2 = (-b + discriminant**0.5) / (2*a)
        return (x1, x2)
    elif discriminant == 0:
        # One real root (repeated root)
        x = -b / (2*a)
        return (x,)
    else:
        # Complex conjugate roots
        # Using cmath.sqrt to handle negative discriminant for complex numbers
        x1 = (-b - cmath.sqrt(discriminant)) / (2 * a)
        x2 = (-b + cmath.sqrt(discriminant)) / (2 * a)
        return (x1, x2)

