import cmath

def calculate_quadratic_roots(a, b, c):
    """
    Calculates the roots of a quadratic equation ax^2 + bx + c = 0.

    Args:
        a (float): Coefficient of x^2.
        b (float): Coefficient of x.
        c (float): Constant term.

    Returns:
        tuple: A tuple containing the roots.
               - If two distinct real roots: (x1, x2)
               - If one real root: (x1,)
               - If complex conjugate roots: (complex_x1, complex_x2)
               - If 'a' is zero (linear equation): (x,) or "No solution"
    """
    if a == 0:
        if b == 0:
            if c == 0:
                return "Infinite solutions (0 = 0)"
            else:
                return "No solution (c != 0, but 0x = -c)"
        else:
            x = -c / b
            return (x,) # Linear equation

    delta = (b**2) - 4*(a*c)

    if delta > 0:
        x1 = (-b - delta**0.5) / (2*a)
        x2 = (-b + delta**0.5) / (2*a)
        return (x1, x2)
    elif delta == 0:
        x = -b / (2*a)
        return (x,)
    else:
        # Complex roots
        x1 = (-b - cmath.sqrt(delta)) / (2 * a)
        x2 = (-b + cmath.sqrt(delta)) / (2 * a)
        return (x1, x2)

# Get input from the user
try:
    a, b, c = map(float, input("Enter coefficients a, b and c: ").split())
except ValueError:
    print("Invalid input. Please enter three numbers separated by spaces.")
else:
    roots = calculate_quadratic_roots(a, b, c)

