import abc

class SortAlgorithm(abc.ABC):
    def sort(self, data):
        n = len(data)
        for i in range(n):
            for j in range(0, n-i-1):
                if self.compare(data[j], data[j+1]):
                    self.swap(data, j, j+1)

    @abc.abstractmethod
    def compare(self, a, b):
        pass

    @abc.abstractmethod
    def swap(self, data, i, j):
        pass

class BubbleSort(SortAlgorithm):
    def compare(self, a, b):
        return a > b

    def swap(self, data, i, j):
        data[i], data[j] = data[j], data[i]

class SelectionSort(SortAlgorithm):
    def compare(self, a, b):
        return a > b

    def swap(self, data, i, j):
        data[i], data[j] = data[j], data[i]

# Test Cases
if __name__ == '__main__':
    # Integer Array
    data_int = [5, 1, 4, 2, 8]
    bubble_sort = BubbleSort()
    selection_sort = SelectionSort()

    bubble_sort.sort(data_int)
    print("Bubble Sort (Integers):", data_int)  # Output: [1, 2, 4, 5, 8]

    data_int = [5, 1, 4, 2, 8] # Reset for next sort
    selection_sort.sort(data_int)
    print("Selection Sort (Integers):", data_int)  # Output: [1, 2, 4, 5, 8]

    # String Array
    data_str = ["banana", "apple", "cherry", "date"]
    bubble_sort = BubbleSort()
    selection_sort = SelectionSort()

    bubble_sort.sort(data_str)
    print("Bubble Sort (Strings):", data_str)  # Output: ['apple', 'banana', 'cherry', 'date']

    data_str = ["banana", "apple", "cherry", "date"] # Reset for next sort
    selection_sort.sort(data_str)
    print("Selection Sort (Strings):", data_str)  # Output: ['apple', 'banana', 'cherry', 'date']
