from abc import ABC, abstractmethod

class SortAlgorithm(ABC):
    def sort(self, data):
        n = len(data)
        for i in range(n):
            for j in range(0, n-i-1):
                if self.compare(data[j], data[j+1]):
                    self.swap(data, j, j+1)

    @abstractmethod
    def compare(self, a, b):
        pass

    @abstractmethod
    def swap(self, data, i, j):
        pass

class BubbleSort(SortAlgorithm):
    def compare(self, a, b):
        return a > b

    def swap(self, data, i, j):
        data[i], data[j] = data[j], data[i]

class SelectionSort(SortAlgorithm):
    def compare(self, a, b):
        return a > b

    def swap(self, data, i, j):
        data[i], data[j] = data[j], data[i]

# Test the implementation
data_int = [5, 1, 4, 2, 8]
bubble_sort = BubbleSort()
selection_sort = SelectionSort()

bubble_sort.sort(data_int)
print("Bubble Sort:", data_int)

data_int = [5, 1, 4, 2, 8]
selection_sort.sort(data_int)
print("Selection Sort:", data_int)

data_str = ["banana", "apple", "cherry", "date"]
bubble_sort.sort(data_str)
print("Bubble Sort (strings):", data_str)

data_str = ["banana", "apple", "cherry", "date"]
selection_sort.sort(data_str)
print("Selection Sort (strings):", data_str)
```

