from abc import ABC, abstractmethod

class SortAlgorithm(ABC):
    def sort(self, data):
        n = len(data)
        for i in range(n):
            for j in range(0, n-i-1):
                if self.compare(data[j], data[j+1]):
                    self.swap(data, j, j+1)

    @abstractmethod
    def compare(self, a, b):
        pass

    @abstractmethod
    def swap(self, data, i, j):
        pass

class BubbleSort(SortAlgorithm):
    def compare(self, a, b):
        return a > b

    def swap(self, data, i, j):
        data[i], data[j] = data[j], data[i]

class SelectionSort(SortAlgorithm):
    def compare(self, a, b):
        return a > b

    def swap(self, data, i, j):
        data[i], data[j] = data[j], data[i]

# Test Cases
if __name__ == '__main__':
    data1 = [5, 1, 4, 2, 8]
    bubble_sort = BubbleSort()
    bubble_sort.sort(data1)
    print("Bubble Sort:", data1)

    data2 = [5, 1, 4, 2, 8]
    selection_sort = SelectionSort()
    selection_sort.sort(data2)
    print("Selection Sort:", data2)

    data3 = ["banana", "apple", "cherry"]
    bubble_sort.sort(data3)
    print("Bubble Sort (Strings):", data3)

    data4 = ["banana", "apple", "cherry"]
    selection_sort.sort(data4)
    print("Selection Sort (Strings):", data4)
