```python
from abc import ABC, abstractmethod

class SortAlgorithm(ABC):
    def __init__(self, data):
        self.data = data

    @abstractmethod
    def compare(self, i, j):
        pass

    @abstractmethod
    def swap(self, i, j):
        pass

    def sort(self):
        n = len(self.data)
        for i in range(n):
            for j in range(0, n-i-1):
                if self.compare(j, j+1):
                    self.swap(j, j+1)

class BubbleSort(SortAlgorithm):
    def compare(self, i, j):
        return self.data[i] > self.data[j]

    def swap(self, i, j):
        self.data[i], self.data[j] = self.data[j], self.data[i]

class SelectionSort(SortAlgorithm):
    def compare(self, i, j):
        return self.data[i] > self.data[j]

    def swap(self, i, j):
        self.data[i], self.data[j] = self.data[j], self.data[i]

# Test cases
if __name__ == '__main__':
    # Integer array
    int_array = [5, 1, 4, 2, 8]
    bubble_sort = BubbleSort(int_array.copy())
    bubble_sort.sort()
    print("Bubble Sort (Integers):", bubble_sort.data)

    selection_sort = SelectionSort(int_array.copy())
    selection_sort.sort()
    print("Selection Sort (Integers):", selection_sort.data)

    # String array
    string_array = ["banana", "apple", "cherry", "date"]
    bubble_sort_str = BubbleSort(string_array.copy())
    bubble_sort_str.sort()
    print("Bubble Sort (Strings):", bubble_sort_str.data)

    selection_sort_str = SelectionSort(string_array.copy())
    selection_sort_str.sort()
    print("Selection Sort (Strings):", selection_sort_str.data)
```

