from abc import ABC, abstractmethod

class SortAlgorithm(ABC):
    def sort(self, data):
        n = len(data)
        for i in range(n):
            for j in range(0, n-i-1):
                if self.compare(data[j], data[j+1]):
                    self.swap(data, j, j+1)

    @abstractmethod
    def compare(self, a, b):
        pass

    @abstractmethod
    def swap(self, data, i, j):
        pass

class BubbleSort(SortAlgorithm):
    def compare(self, a, b):
        return a > b

    def swap(self, data, i, j):
        data[i], data[j] = data[j], data[i]

class SelectionSort(SortAlgorithm):
    def compare(self, a, b):
        return a > b

    def swap(self, data, i, j):
        data[i], data[j] = data[j], data[i]

# Test Cases
if __name__ == '__main__':
    # Integer array
    int_array = [64, 34, 25, 12, 22, 11, 90]
    bubble_sort = BubbleSort()
    selection_sort = SelectionSort()

    bubble_sort.sort(int_array)
    print("Bubble Sort (Integers):", int_array)

    int_array = [64, 34, 25, 12, 22, 11, 90] # Reset array
    selection_sort.sort(int_array)
    print("Selection Sort (Integers):", int_array)

    # String array
    string_array = ["banana", "apple", "orange", "grape"]
    bubble_sort = BubbleSort()
    selection_sort = SelectionSort()

    bubble_sort.sort(string_array)
    print("Bubble Sort (Strings):", string_array)

    string_array = ["banana", "apple", "orange", "grape"] # Reset array
    selection_sort.sort(string_array)
    print("Selection Sort (Strings):", string_array)
