import yfinance as yf

def get_stock_price(ticker, date):
    try:
        data = yf.download(ticker, start=date, end=date)
        if not data.empty:
            return data['Close'][0]
        else:
            return None
    except Exception as e:
        print(f"Error fetching data for {ticker}: {e}")
        return None

apple_price = get_stock_price("AAPL", "2019-12-31")
amazon_price = get_stock_price("AMZN", "2019-12-31")

if apple_price is not None and amazon_price is not None:
    apple_price_formatted = "${:.2f}".format(apple_price)
    amazon_price_formatted = "${:.2f}".format(amazon_price)

    if apple_price > amazon_price:
        print(f"Apple Inc. had a higher market value on December 31st, 2019, with a closing price of {apple_price_formatted}.")
    elif amazon_price > apple_price:
        print(f"Amazon Inc. had a higher market value on December 31st, 2019, with a closing price of {amazon_price_formatted}.")
    else:
        print("Both companies had the same market value on December 31st, 2019.")
else:
    print("Could not retrieve stock prices for both companies.")
