class SortAlgorithm:
    def sort(self):
        raise NotImplementedError("Subclasses must implement the sort() method")

    def compare(self, a, b):
        raise NotImplementedError("Subclasses must implement the compare() method")

    def swap(self, a, b):
        raise NotImplementedError("Subclasses must implement the swap() method")


class BubbleSort(SortAlgorithm):
    def sort(self):
        n = len(self.data)
        for i in range(n):
            for j in range(0, n-i-1):
                if self.compare(self.data[j], self.data[j+1]) > 0:
                    self.swap(self.data[j], self.data[j+1])

class SelectionSort(SortAlgorithm):
    def sort(self):
        n = len(self.data)
        for i in range(n):
            min_idx = i
            for j in range(i+1, n):
                if self.compare(self.data[j], self.data[min_idx]) < 0:
                    min_idx = j
            self.swap(self.data[i], self.data[min_idx])


if __name__ == '__main__':
    # Test BubbleSort
    data_bubble = [64, 34, 25, 12, 22, 11, 90]
    bubble_sort = BubbleSort()
    bubble_sort.data = data_bubble
    bubble_sort.sort()
    print("Bubble Sort:", bubble_sort.data)

    # Test SelectionSort
    data_selection = [64, 34, 25, 12, 22, 11, 90]
    selection_sort = SelectionSort()
    selection_sort.data = data_selection
    selection_sort.sort()
    print("Selection Sort:", selection_sort.data)
