class SelectionSort(SortAlgorithm):
    def sort(self):
        n = len(arr)
        for i in range(n):
            min_idx = i
            for j in range(i+1, n):
                if self.compare(arr[j], arr[min_idx]) < 0:
                    min_idx = j
            self.swap(arr[i], arr[min_idx])
