class SortAlgorithm:
    def sort(self, arr):
        """Template method for sorting an array."""
        for i in range(len(arr) - 1):
            for j in range(len(arr) - i - 1):
                if self.compare(arr[j], arr[j+1]):
                    self.swap(arr, j, j+1)
        return arr

    def compare(self, a, b):
